C++<pre><code>class Solution {
public:
    bool validateBinaryTreeNodes(int n, vector&lt;int&gt;&amp; leftChild, vector&lt;int&gt;&amp; rightChild) {
        vector&lt;vector&lt;int&gt;&gt; adj(n);
        vector&lt;int&gt; in(n, 0);
        for (int u = 0; u &lt; n; u++) {
            int v = leftChild[u];
            if (v == -1) continue;
            adj[u].push_back(v);
            in[v]++;
        }
        for (int u = 0; u &lt; n; u++) {
            int v = rightChild[u];
            if (v == -1) continue;
            adj[u].push_back(v);
            in[v]++;
        }

        int cnt = 0;
        int u = 0;

        for (int i = 0; i &lt; n; i++) {
            if (in[i] == 0) {
                cnt++;
                u = i;
            }
        }
        if (cnt != 1) {
            return false;
        }

        queue&lt;int&gt; q;
        vector&lt;int&gt; vis(n, 0);
        q.push(u);
        vis[u] = 1;
        cnt = 1;
        while (q.size()) {
            int p = q.front();
            q.pop();
            for (auto&amp; v : adj[p]) {
                cnt++;
                if (!vis[v]) {
                    vis[v] = 1;
                    q.push(v);
                }
            }
        }

        return cnt == n;
    }
};
</code></pre>

