Java<pre><code>class ProducerConsumer {
    private final int capacity;
    private final Queue&lt;Integer&gt; queue = new LinkedList&lt;&gt;();

    public ProducerConsumer(int capacity) {
        this.capacity = capacity;
    }

    // 生产者方法
    public synchronized void produce(int item) throws InterruptedException {
        while (queue.size() == capacity) {
            wait();
        }
        queue.add(item);
        System.out.println("Produced: " + item);
        notifyAll();
    }

    // 消费者方法
    public synchronized int consume() throws InterruptedException {
        while (queue.isEmpty()) {
            wait();
        }
        int item = queue.poll();
        System.out.println("Consumed: " + item);
        notifyAll();
        return item;
    }
}

class Producer implements Runnable {
    private final ProducerConsumer pc;
    private int count;

    public Producer(ProducerConsumer pc) {
        this.pc = pc;
    }

    @Override
    public void run() {
        try {
            while (count &lt; 10) {
                pc.produce(count++);
                Thread.sleep(100);
            }
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
    }
}

class Consumer implements Runnable {
    private final ProducerConsumer pc;

    public Consumer(ProducerConsumer pc) {
        this.pc = pc;
    }

    @Override
    public void run() {
        try {
            for (int i = 0; i &lt; 10; i++) {
                pc.consume();
                Thread.sleep(100);
            }
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
    }
}

public class Main {
    public static void main(String[] args) {
        ProducerConsumer pc = new ProducerConsumer(5);
        Thread producerThread = new Thread(new Producer(pc));
        Thread consumerThread = new Thread(new Consumer(pc));

        producerThread.start();
        consumerThread.start();
    }
}
</code></pre>

Java<pre><code>public class ByteFIFOBuffer {
    private byte[] buffer;
    private int readIndex;
    private int writeIndex;

    public ByteFIFOBuffer(int size) {
        buffer = new byte[size];
        readIndex = 0;
        writeIndex = 0;
    }

    public synchronized void put(byte b) {
        buffer[writeIndex] = b;
        writeIndex = (writeIndex + 1) % buffer.length;
    }

    public synchronized byte get() {
        byte b = buffer[readIndex];
        readIndex = (readIndex + 1) % buffer.length;
        return b;
    }

    public synchronized boolean isEmpty() {
        return readIndex == writeIndex;
    }

    public synchronized boolean isFull() {
        return (writeIndex + 1) % buffer.length == readIndex;
    }
}
</code></pre>

