void mergeSort(int l, int r, std::vector&lt;int&gt;&amp; a, int&amp; ans) {
    if (l &gt;= r) return;
    int mid = (l + r) &gt;&gt; 1, i = l, j = mid + 1, cnt = 0;
    mergeSort(l, mid, a, ans);
    mergeSort(mid + 1, r, a, ans);
    while (i &lt;= mid || j &lt;= r)
        if (j &gt; r || (i &lt;= mid &amp;&amp; a[i] &lt;= a[j]))
            tmp[cnt++] = a[i++];
        else
            tmp[cnt++] = a[j++], ans += mid - i + 1;
