using Z = MInt&lt;P&gt;;

struct Comb {
    int n;
    std::vector&lt;Z&gt; _fac;
    std::vector&lt;Z&gt; _invfac;
    std::vector&lt;Z&gt; _inv;

    Comb() : n{0}, _fac{1}, _invfac{1}, _inv{0} {}
    Comb(int n) : Comb() {
        init(n);
    }

    void init(int m) {
        m = std::min&lt;i64&gt;(m, Z::getMod() - 1);
        if (m &lt;= n) return;
        _fac.resize(m + 1);
        _invfac.resize(m + 1);
        _inv.resize(m + 1);

        for (int i = n + 1; i &lt;= m; i++) {
            _fac[i] = _fac[i - 1] * i;
        }
        _invfac[m] = _fac[m].inv();
        for (int i = m; i &gt; n; i--) {
            _invfac[i - 1] = _invfac[i] * i;
            _inv[i] = _invfac[i] * _fac[i - 1];
        }
        n = m;
    }

    Z fac(int m) {
        if (m &gt; n) init(2 * m);
        return _fac[m];
    }
    Z invfac(int m) {
        if (m &gt; n) init(2 * m);
        return _invfac[m];
    }
    Z inv(int m) {
        if (m &gt; n) init(2 * m);
        return _inv[m];
    }
    Z binom(int n, int m) {
        if (n &lt; m || m &lt; 0) return 0;
        return fac(n) * invfac(m) * invfac(n - m);
    }
} comb;
