C++<pre><code>int lazy[N &lt;&lt; 3];  // 标记了这条线段出现的次数
double s[N &lt;&lt; 3];

struct node1 {
  double l, r;
  double sum;
} cl[N &lt;&lt; 3];  // 线段树

struct node2 {
  double x, y1, y2;
  int flag;
} p[N &lt;&lt; 3];  // 坐标

// 定义sort比较
bool cmp(node2 a, node2 b) { return a.x &lt; b.x; }

// 上传
void pushup(int rt) {
  if (lazy[rt] &gt; 0)
    cl[rt].sum = cl[rt].r - cl[rt].l;
  else
    cl[rt].sum = cl[rt * 2].sum + cl[rt * 2 + 1].sum;
}

// 建树
void build(int rt, int l, int r) {
  if (r - l &gt; 1) {
    cl[rt].l = s[l];
    cl[rt].r = s[r];
    build(rt * 2, l, (l + r) / 2);
    build(rt * 2 + 1, (l + r) / 2, r);
    pushup(rt);
  } else {
    cl[rt].l = s[l];
    cl[rt].r = s[r];
    cl[rt].sum = 0;
  }
  return;
}

// 更新
void update(int rt, double y1, double y2, int flag) {
  if (cl[rt].l == y1 &amp;&amp; cl[rt].r == y2) {
    lazy[rt] += flag;
    pushup(rt);
    return;
  } else {
    if (cl[rt * 2].r &gt; y1) update(rt * 2, y1, min(cl[rt * 2].r, y2), flag);
    if (cl[rt * 2 + 1].l &lt; y2)
      update(rt * 2 + 1, max(cl[rt * 2 + 1].l, y1), y2, flag);
    pushup(rt);
  }
}

void solve() {
    int temp = 1, n;
    double x1, y1, x2, y2, ans;
    cin &gt;&gt; n;
    ans = 0;
    for (int i = 0; i &lt; n; i++) {
        scanf("%lf %lf %lf %lf", &amp;x1, &amp;y1, &amp;x2, &amp;y2);
        p[i].x = x1;
        p[i].y1 = y1;
        p[i].y2 = y2;
        p[i].flag = 1;
        p[i + n].x = x2;
        p[i + n].y1 = y1;
        p[i + n].y2 = y2;
        p[i + n].flag = -1;
        s[i + 1] = y1;
        s[i + n + 1] = y2;
    }
    sort(s + 1, s + (2 * n + 1));  // 离散化
    sort(p, p + 2 * n, cmp);  // 把矩形的边的横坐标从小到大排序
    build(1, 1, 2 * n);       // 建树
    memset(lazy, 0, sizeof(lazy));
    update(1, p[0].y1, p[0].y2, p[0].flag);
    for (int i = 1; i &lt; 2 * n; i++) {
        ans += (p[i].x - p[i - 1].x) * cl[1].sum;
        update(1, p[i].y1, p[i].y2, p[i].flag);
    }
    cout &lt;&lt; ans &lt;&lt; '\n';
}
