C++<pre><code>template&lt;class T&gt; struct Segt {
    struct node {
        int l, r;
        T w, rmq, lazy;
    };
    std::vector&lt;T&gt; w;
    std::vector&lt;node&gt; t;

    Segt() {}
    Segt(int n) { init(n); }
    Segt(std::vector&lt;int&gt; in) {
        int n = in.size() - 1;
        w.resize(n + 1);
        for (int i = 1; i &lt;= n; i++) {
            w[i] = in[i];
        }
        init(in.size() - 1);
    }

    #define GL (k &lt;&lt; 1)
    #define GR (k &lt;&lt; 1 | 1)

    void init(int n) {
        w.resize(n + 1);
        t.resize(n * 4 + 1);
        auto build = [&amp;](auto self, int l, int r, int k = 1) {
            if (l == r) {
                t[k] = {l, r, w[l], w[l], -1};
                return;
            }
            t[k] = {l, r};
            int mid = (l + r) / 2;
            self(self, l, mid, GL);
            self(self, mid + 1, r, GR);
            pushup(k);
        };
        build(build, 1, n);
    }
    void pushdown(node &amp;p, T lazy) {
        p.w += (p.r - p.l + 1) * lazy;
        p.rmq += lazy;
        p.lazy += lazy;
    }
    void pushdown(int k) {
        if (t[k].lazy == -1) return;
        pushdown(t[GL], t[k].lazy);
        pushdown(t[GR], t[k].lazy);
        t[k].lazy = -1;
    }
    void pushup(int k) {
        auto pushup = [&amp;](node &amp;p, node &amp;l, node &amp;r) {
            p.w = l.w + r.w;
            p.rmq = std::min(l.rmq, r.rmq); // RMQ -&gt; min/max
        };
        pushup(t[k], t[GL], t[GR]);
    }
    void modify(int l, int r, T val, int k = 1) {
        if (l &lt;= t[k].l &amp;&amp; t[k].r &lt;= r) {
            pushdown(t[k], val);
            return;
        }
        pushdown(k);
        int mid = (t[k].l + t[k].r) / 2;
        if (l &lt;= mid) modify(l, r, val, GL);
        if (mid &lt; r) modify(l, r, val, GR);
        pushup(k);
    }
    T rmq(int l, int r, int k = 1) {
        if (l &lt;= t[k].l &amp;&amp; t[k].r &lt;= r) {
            return t[k].rmq;
        }
        pushdown(k);
        int mid = (t[k].l + t[k].r) / 2;
        T ans = std::numeric_limits&lt;T&gt;::max(); // RMQ -&gt; 为 max 时需要修改为 ::lowest()
        if (l &lt;= mid) ans = std::min(ans, rmq(l, r, GL)); // RMQ -&gt; min/max
        if (mid &lt; r) ans = std::min(ans, rmq(l, r, GR)); // RMQ -&gt; min/max
        return ans;
    }
    T ask(int l, int r, int k = 1) { // 区间询问
        if (l &lt;= t[k].l &amp;&amp; t[k].r &lt;= r) {
            return t[k].w;
        }
        pushdown(k);
        int mid = (t[k].l + t[k].r) / 2;
        T ans = 0;
        if (l &lt;= mid) ans += ask(l, r, GL);
        if (mid &lt; r) ans += ask(l, r, GR);
        return ans;
    }
    void debug(int k = 1) {
        std::cout &lt;&lt; "[" &lt;&lt; t[k].l &lt;&lt; ", " &lt;&lt; t[k].r &lt;&lt; "]: ";
        std::cout &lt;&lt; "w = " &lt;&lt; t[k].w &lt;&lt; ", ";
        std::cout &lt;&lt; "Min = " &lt;&lt; t[k].rmq &lt;&lt; ", ";
        std::cout &lt;&lt; "lazy = " &lt;&lt; t[k].lazy &lt;&lt; ", ";
        std::cout &lt;&lt; std::endl;
        if (t[k].l == t[k].r) return;
        debug(GL), debug(GR);
    }
};
</code></pre>

C++<pre><code>template &lt;class T&gt; struct Segt_ {
