        w.resize(n + 1);
        t.resize((n &lt;&lt; 2) + 1);
        build(1, n);
    }
    Segt_(vector&lt;int&gt; in) {
        int n = in.size() - 1;
        w.resize(n + 1);
        for (int i = 1; i &lt;= n; i++) {
            w[i] = in[i];
        }
        t.resize((n &lt;&lt; 2) + 1);
        build(1, n);
    }
    void pushdown(node &amp;p, T add, T mul) {
        p.w = p.w * mul + (p.r - p.l + 1) * add;
        p.add = p.add * mul + add;
        p.mul *= mul;
    }
    void pushup(node &amp;p, node &amp;l, node &amp;r) {
        p.w = l.w + r.w;
    }
#define GL (k &lt;&lt; 1)
#define GR (k &lt;&lt; 1 | 1)
    void pushdown(int k) { // 不需要动
        pushdown(t[GL], t[k].add, t[k].mul);
        pushdown(t[GR], t[k].add, t[k].mul);
        t[k].add = 0, t[k].mul = 1;
    }
    void pushup(int k) { // 不需要动
        pushup(t[k], t[GL], t[GR]);
    }
    void build(int l, int r, int k = 1) {
        if (l == r) {
            t[k] = {l, r, w[l]};
            return;
        }
        t[k] = {l, r};
        int mid = (l + r) / 2;
        build(l, mid, GL);
        build(mid + 1, r, GR);
        pushup(k);
    }
    void modify(int l, int r, T val, int k = 1) { // 区间修改
        if (l &lt;= t[k].l &amp;&amp; t[k].r &lt;= r) {
            t[k].w += (t[k].r - t[k].l + 1) * val;
            t[k].add += val;
            return;
        }
        pushdown(k);
        int mid = (t[k].l + t[k].r) / 2;
        if (l &lt;= mid) modify(l, r, val, GL);
        if (mid &lt; r) modify(l, r, val, GR);
        pushup(k);
    }
    void modify2(int l, int r, T val, int k = 1) { // 区间修改
        if (l &lt;= t[k].l &amp;&amp; t[k].r &lt;= r) {
            t[k].w *= val;
            t[k].add *= val;
            t[k].mul *= val;
            return;
        }
        pushdown(k);
        int mid = (t[k].l + t[k].r) / 2;
        if (l &lt;= mid) modify2(l, r, val, GL);
        if (mid &lt; r) modify2(l, r, val, GR);
        pushup(k);
