C++<pre><code>struct segmentTree {
#define ls (u &lt;&lt; 1)
#define rs (u &lt;&lt; 1 | 1)
#define mid ((l + r) &gt;&gt; 1)

    int s[N &lt;&lt; 2]{}, tag[N &lt;&lt; 2]{}, siz[N &lt;&lt; 2]{}, mx[N &lt;&lt; 2]{};

    inline void build(int u, int l, int r) {
        if (l == r) {
            s[u] = a[l];
            siz[u] = 1;
            tag[u] = 0;
            mx[u] = a[l];
            return;
        }
        if (l &lt;= mid) build(ls, l, mid);
        if (mid &lt; r) build(rs, mid + 1, r);
        pushup(u);
    }
    inline void pushdown(int u) {
        if (tag[u] != 0) {
            tag[ls] += tag[u];
            tag[rs] += tag[u];
            s[ls] += siz[ls] * tag[u];
            s[rs] += siz[rs] * tag[u];
            mx[ls] += tag[u];
            mx[rs] += tag[u];
        }
        tag[u] = 0;
    }
    inline void pushup(int u) {
        s[u] = s[ls] + s[rs];
        siz[u] = siz[ls] + siz[rs];
        mx[u] = max(mx[ls], mx[rs]);
    }
    inline void modify(int u, int l, int r, int ql, int qr, int k) {
        if (ql &lt;= l &amp;&amp; r &lt;= qr) {
            s[u] += siz[u] * k;
            mx[u] += k;
            tag[u] += k;
            return;
        }
        pushdown(u);
        if (ql &lt;= mid) modify(ls, l, mid, ql, qr, k);
        if (qr &gt; mid) modify(rs, mid + 1, r, ql, qr, k);
        pushup(u);
    }
    inline int query(int u, int l, int r, int ql, int qr) {
        if (ql &lt;= l &amp;&amp; r &lt;= qr) {
            return s[u];
        }
        pushdown(u);
        int res = 0;
        if (ql &lt;= mid) res = query(ls, l, mid, ql, qr);
        if (qr &gt; mid) res += query(rs, mid + 1, r, ql, qr);
        return res;
    }
} T;
