void manacher() {
    int R = 0, c;
    for (int i = 1; i &lt; n; i++) {
        if (i &lt; R) P[i] = min(P[(c &lt;&lt; 1) - i], P[c] + c - i);
        else P[i] = 1;
        while (S[i + P[i]] == S[i - P[i]]) P[i]++;
        if (P[i] + i &gt; R) {
            R = P[i] + i;
            c = i;
        }
    }
}
