static const int mod2 = 1E9 + 9, base2 = 131;
using U = Zmod&lt;mod1&gt;;
using V = Zmod&lt;mod2&gt;;
vector&lt;U&gt; val1;
vector&lt;V&gt; val2;
void init(int n = N) {
    val1.resize(n + 1), val2.resize(n + 2);
    val1[0] = 1, val2[0] = 1;
    for (int i = 1; i &lt;= n; i++) {
        val1[i] = val1[i - 1] * base1;
        val2[i] = val2[i - 1] * base2;
