#define ll long long
using namespace std;
const int N = 1e5 + 5;
const ll llinf = 0x3f3f3f3f3f3f3f3f;
struct edge {
    int v;
    ll w;
};
struct node {
    ll dis;
    int u;
    bool operator&gt;(const node&amp; a) const { return dis &gt; a.dis; }
};
vector&lt;edge&gt; ed1[N], ed2[N];
ll dis1[N], dis2[N], ans[N], a[N];
bool vis1[N], vis2[N];
int n, m, q;
void dijkstra() {
    memset(dis1, llinf, sizeof(dis1));
    memset(dis2, llinf, sizeof(dis2));
    priority_queue&lt;node, vector&lt;node&gt;, greater&lt;node&gt; &gt; q;
    q.push({0, 1});
    dis1[1] = 0;
    while (!q.empty()) {
        int u = q.top().u;
        q.pop();
        if (vis1[u]) continue;
        vis1[u] = 1;
        for (auto e : ed1[u]) {
            int v = e.v;
            ll w = e.w;
            if (dis1[v] &gt; dis1[u] + w) {
                dis1[v] = dis1[u] + w;
                q.push({dis1[v], v});
            }
        }
    }
    // 也可以使用传参的方式只写一遍，这里直接复制了
    priority_queue&lt;node, vector&lt;node&gt;, greater&lt;node&gt; &gt; qq;
    qq.push({0, n});
    dis2[n] = 0;
    while (!qq.empty()) {
        int u = qq.top().u;
        qq.pop();
        if (vis2[u]) continue;
        vis2[u] = 1;
        for (auto e : ed2[u]) {
            int v = e.v;
            ll w = e.w;
            if (dis2[v] &gt; dis2[u] + w) {
                dis2[v] = dis2[u] + w;
                qq.push({dis2[v], v});
            }
        }
    }
}
map&lt;ll, int&gt; mp;
set&lt;ll&gt; st;
ll mn = llinf;
void solve()
{
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; q;
    while (m--) {
        int u, v;
        ll c, d;
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; c &gt;&gt; d;
        ed1[u].push_back({v, c});
        ed2[v].push_back({u, d}); // 旅游金反向建图
    }
    dijkstra();
    rep(i, 1, n) {
        cin &gt;&gt; a[i];
        if (dis1[i] == llinf || dis2[i] == llinf) { ans[i] = llinf; continue; } // 走不通的不用管
        else ans[i] = dis1[i] + dis2[i] / a[i] + (dis2[i] % a[i] ? 1 : 0); // 向上取整，不能整除就加 1
        mn = min(mn, ans[i]);
        mp[ans[i]]++;
        if (mp[ans[i]] == 1) st.insert(ans[i]);
    }
    while (q--) {
        int x;
        ll b;
        cin &gt;&gt; x &gt;&gt; b;
        if (dis1[x] != llinf &amp;&amp; dis2[x] != llinf) {
            ll t = dis1[x] + dis2[x] / b + (dis2[x] % b ? 1 : 0);
            mp[t]++;
            if (mp[t] == 1) st.insert(t);
            mp[ans[x]]--;
            if (mp[ans[x]] == 0) {
                st.erase(st.find(ans[x])); // 已经没有这个情况了，删除这个数
            }
            mn = *st.begin(); // 更新最小值
            ans[x] = t; // 更新这个点的答案
        }
        cout &lt;&lt; mn &lt;&lt; '\n';
    }
}
int main()
{
    solve();
    return 0;
}
